import React, { useState, useEffect } from "react";
import axios from "axios";

export default function Chat({ userId }) {
  const [text, setText] = useState("");
  const [messages, setMessages] = useState([]);

  useEffect(() => {
    fetchMessages();
  }, []);

  const fetchMessages = async () => {
    try {
      const res = await axios.get(`http://localhost:5285/api/message/${userId}`);
      setMessages(res.data.reverse());
    } catch (err) {
      console.error(err);
    }
  };

  const sendMessage = async () => {
    if (!text) return;
    try {
      const res = await axios.post(`http://localhost:5285/api/message`, {
        userId,
        text,
      });
      setMessages([res.data, ...messages]);
      setText("");
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div style={{ maxWidth: "600px", margin: "auto" }}>
      <h2>Chat</h2>
      <div
        style={{
          border: "1px solid #ccc",
          padding: "10px",
          height: "400px",
          overflowY: "scroll",
          marginBottom: "10px",
        }}
      >
        {messages.map((msg) => (
          <div key={msg.id} style={{ marginBottom: "10px" }}>
            <b>You:</b> {msg.text} <br />
            <span style={{ color: msg.sentiment === "positive" ? "green" : msg.sentiment === "negative" ? "red" : "gray" }}>
              {msg.sentiment} ({msg.score.toFixed(2)})
            </span>
          </div>
        ))}
      </div>
      <input
        type="text"
        value={text}
        onChange={(e) => setText(e.target.value)}
        style={{ width: "80%", padding: "5px" }}
        placeholder="Mesaj yaz..."
      />
      <button onClick={sendMessage} style={{ padding: "5px 10px" }}>GÃ¶nder</button>
    </div>
  );
}
