import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const ChatPage = () => {
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState("");

  const navigate = useNavigate();
  const currentUser = JSON.parse(localStorage.getItem("user"));

  useEffect(() => {
    if (!currentUser) navigate("/");
    else fetchUsers();
  }, []);

  const fetchUsers = async () => {
    const response = await axios.get("http://localhost:5285/api/user");
    setUsers(response.data.filter((u) => u.id !== currentUser.id));
  };

  const handleSelectUser = (e) => {
    const userId = parseInt(e.target.value);
    const user = users.find((u) => u.id === userId);
    setSelectedUser(user);
    setMessages([]); // Seçilen kullanıcı değişince chat sıfırlanır
  };

 const handleSendMessage = async () => {
  if (!newMessage || !selectedUser) return;

  const messageObj = {
    senderId: currentUser.id,
    receiverId: selectedUser.id,
    text: newMessage,
    sentiment: "pending",
    score: 0,
  };

  // Önce frontend listesine ekle
  setMessages((prev) => [...prev, messageObj]);
  setNewMessage("");

  try {
    // 1️⃣ Backend'e kaydet
    await axios.post("https://localhost:5285/api/message", {
      senderId: currentUser.id,
      receiverId: selectedUser.id,
      text: newMessage,
    });

    // 2️⃣ AI servisine gönder (Hugging Face)
    const hfResponse = await axios.post("https://sevvaltzl-sentimentt.hf.space/api/predict", {
      data: [newMessage],
    });

    const result = hfResponse.data.data?.[0]; // Hugging Face formatı: ["positive", 0.91]

    // Frontend mesaj listesini güncelle
    setMessages((prev) =>
      prev.map((m, i) =>
        i === prev.length - 1
          ? { ...m, sentiment: result[0], score: result[1] }
          : m
      )
    );
  } catch (err) {
    console.error(err);
    // AI veya backend hatası varsa sentiment = error
    setMessages((prev) =>
      prev.map((m, i) =>
        i === prev.length - 1 ? { ...m, sentiment: "error", score: 0 } : m
      )
    );
  }
};

  return (
    <div style={styles.container}>
      <h2>Hoş geldin, {currentUser?.nickname}</h2>

      <div style={{ margin: "20px 0" }}>
        <label>Mesajlaşmak istediğin kullanıcı: </label>
        <select onChange={handleSelectUser} value={selectedUser?.id || ""}>
          <option value="">Seçiniz</option>
          {users.map((u) => (
            <option key={u.id} value={u.id}>
              {u.nickname}
            </option>
          ))}
        </select>
      </div>

      {selectedUser && (
        <div style={styles.chatContainer}>
          <div style={styles.messages}>
            {messages.map((m, index) => (
              <div key={index} style={m.senderId === currentUser.id ? styles.myMessage : styles.theirMessage}>
                <strong>{m.senderId === currentUser.id ? "You" : selectedUser.nickname}:</strong> {m.text}
                {m.sentiment && m.sentiment !== "pending" && (
                  <span style={{ marginLeft: "10px", fontStyle: "italic" }}>
                    ({m.sentiment} {m.score.toFixed(2)})
                  </span>
                )}
              </div>
            ))}
          </div>

          <div style={styles.inputContainer}>
            <input
              type="text"
              placeholder="Mesaj yaz..."
              value={newMessage}
              onChange={(e) => setNewMessage(e.target.value)}
              style={styles.input}
            />
            <button onClick={handleSendMessage} style={styles.sendButton}>
              Gönder
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

const styles = {
  container: {
    padding: "20px",
  },
  chatContainer: {
    border: "1px solid #ccc",
    borderRadius: "8px",
    padding: "10px",
    maxWidth: "500px",
  },
  messages: {
    maxHeight: "300px",
    overflowY: "auto",
    marginBottom: "10px",
  },
  myMessage: {
    textAlign: "right",
    background: "#d1ffd6",
    padding: "5px",
    borderRadius: "5px",
    marginBottom: "5px",
  },
  theirMessage: {
    textAlign: "left",
    background: "#f1f1f1",
    padding: "5px",
    borderRadius: "5px",
    marginBottom: "5px",
  },
  inputContainer: {
    display: "flex",
  },
  input: {
    flex: 1,
    padding: "8px",
    fontSize: "14px",
  },
  sendButton: {
    padding: "8px 12px",
    marginLeft: "5px",
    cursor: "pointer",
  },
};

export default ChatPage;
