import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const ChatPage = () => {
  const [users, setUsers] = useState([]);
  const navigate = useNavigate();

  const currentUser = JSON.parse(localStorage.getItem("user"));

  useEffect(() => {
    if (!currentUser) navigate("/");
    else fetchUsers();
  }, []);

  const fetchUsers = async () => {
    const response = await axios.get("http://localhost:5285/api/user");
    setUsers(response.data.filter((u) => u.id !== currentUser.id));
  };

  return (
    <div style={styles.container}>
      <h2>Hoş geldin, {currentUser?.nickname}</h2>
      <h4>Mesajlaşabileceğin kişiler:</h4>
      <ul style={styles.list}>
        {users.map((u) => (
          <li key={u.id} style={styles.userItem}>
            {u.nickname}
          </li>
        ))}
      </ul>
    </div>
  );
};

const styles = {
  container: {
    padding: "20px",
    textAlign: "center",
  },
  list: {
    listStyle: "none",
    padding: 0,
  },
  userItem: {
    padding: "10px",
    margin: "5px",
    background: "#f1f1f1",
    borderRadius: "8px",
  },
};

export default ChatPage;
