import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Login.css"; // CSS ayrı dosyada olacak

function Login({ onLogin }) {
  const [nickname, setNickname] = useState("");
  const [users, setUsers] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:5285/api/user")
      .then(res => setUsers(res.data))
      .catch(err => console.error(err));
  }, []);

  const handleLogin = () => {
    const existingUser = users.find(u => u.nickname === nickname);
    if (existingUser) {
      onLogin(existingUser);
    } else {
      axios.post("http://localhost:5285/api/user/register", { nickname })
        .then(res => onLogin(res.data))
        .catch(err => console.error(err));
    }
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <h2>Rumuz ile giriş</h2>
        <input
          type="text"
          value={nickname}
          onChange={(e) => setNickname(e.target.value)}
          placeholder="Rumuz"
        />
        <button onClick={handleLogin}>Giriş</button>
      </div>
    </div>
  );
}

export default Login;
