import React, { useState, useEffect } from "react";
import axios from "axios";

function Chat({ currentUser }) {
  const [messages, setMessages] = useState([]);
  const [text, setText] = useState("");
  const [users, setUsers] = useState([]);
  const [receiverId, setReceiverId] = useState("");

  useEffect(() => {
    // Tüm kullanıcıları çek (alıcı listesi)
    axios.get("http://localhost:5285/api/user")
      .then(res => setUsers(res.data.filter(u => u.id !== currentUser.id)))
      .catch(err => console.error(err));

    // Mevcut kullanıcı mesajlarını çek
    fetchMessages();
  }, []);

  const fetchMessages = () => {
    if (!receiverId) return;

    axios.get(`http://localhost:5285/api/message?user1=${currentUser.id}&user2=${receiverId}`)
      .then(res => setMessages(res.data))
      .catch(err => console.error(err));
  };

  const handleSend = () => {
    if (!text || !receiverId) return;

    const payload = {
      text,
      senderId: currentUser.id,
      receiverId: parseInt(receiverId)
    };

    axios.post("http://localhost:5285/api/message", payload)
      .then(res => {
        setText("");
        fetchMessages();
      })
      .catch(err => console.error(err));
  };

  useEffect(() => {
    fetchMessages();

    // 3 saniyede bir mesajları güncelle
    const interval = setInterval(fetchMessages, 3000);
    return () => clearInterval(interval);
  }, [receiverId]);

  return (
    <div>
      <h3>Hoşgeldin, {currentUser.nickname}</h3>

      <select value={receiverId} onChange={e => setReceiverId(e.target.value)}>
        <option value="">Mesaj gönderecek kullanıcıyı seç</option>
        {users.map(u => (
          <option key={u.id} value={u.id}>{u.nickname}</option>
        ))}
      </select>

      <div style={{ border: "1px solid black", height: "300px", overflowY: "scroll", margin: "10px 0" }}>
        {messages.map(m => (
          <div key={m.id}>
            <b>{m.senderId === currentUser.id ? "You" : "Them"}:</b> {m.text} 
            <span> ({m.sentiment} {m.score.toFixed(2)})</span>
          </div>
        ))}
      </div>

      <input
        type="text"
        value={text}
        onChange={e => setText(e.target.value)}
        placeholder="Mesaj yaz..."
      />
      <button onClick={handleSend}>Gönder</button>
    </div>
  );
}

export default Chat;
