import React, { useState, useEffect } from "react";
import axios from "axios";

export default function Chat({ user }) {
  const [text, setText] = useState("");
  const [receiverId, setReceiverId] = useState("");
  const [messages, setMessages] = useState([]);
  const [users, setUsers] = useState([]);

  const BASE_URL = "http://localhost:5285";

  // Tüm kullanıcıları çek (kendi kullanıcıyı hariç tut)
  useEffect(() => {
    const fetchUsers = async () => {
      const res = await axios.get(`${BASE_URL}/api/user`);
      setUsers(res.data.filter((u) => u.id !== user.id));
    };
    fetchUsers();
  }, []);

  // Seçilen kullanıcıyla olan mesajları çek
  const fetchMessages = async (rid) => {
    if (!rid) return;
    const res = await axios.get(`${BASE_URL}/api/message/${user.id}/${rid}`);
    setMessages(res.data.reverse());
  };

  // Mesaj gönder
  const sendMessage = async () => {
    if (!text || !receiverId) return;
    const res = await axios.post(`${BASE_URL}/api/message`, {
      senderId: user.id,
      receiverId,
      text,
    });
    setMessages([...messages, res.data]);
    setText("");
  };

  return (
    <div style={{ maxWidth: "600px", margin: "auto" }}>
      <h3>Hoşgeldin, {user.Nickname}</h3>

      <select
        value={receiverId}
        onChange={(e) => {
          setReceiverId(e.target.value);
          fetchMessages(e.target.value);
        }}
      >
        <option value="">Sohbet edeceğin kişiyi seç</option>
        {users.map((u) => (
          <option key={u.id} value={u.id}>{u.nickname}</option>
        ))}
      </select>

      <div style={{
        border: "1px solid #ccc",
        padding: "10px",
        height: "400px",
        overflowY: "scroll",
        marginTop: "10px"
      }}>
        {messages.map((msg) => (
          <div key={msg.id} style={{ marginBottom: "10px" }}>
            <b>{msg.senderId === user.id ? "Sen" : "Karşı Taraf"}:</b> {msg.text}
            <br />
            <span style={{
              color: msg.sentiment === "positive" ? "green" :
                     msg.sentiment === "negative" ? "red" : "gray"
            }}>
              {msg.sentiment} ({msg.score.toFixed(2)})
            </span>
          </div>
        ))}
      </div>

      <input
        type="text"
        value={text}
        onChange={(e) => setText(e.target.value)}
        placeholder="Mesaj yaz..."
        style={{ width: "80%", padding: "5px", marginTop: "10px" }}
      />
      <button onClick={sendMessage}>Gönder</button>
    </div>
  );
}
